import React, { useState, useEffect, useMemo } from "react";
import ParameterSlider from "./components/parameterSlider";
import ToggleSwitch from "./components/ToggleSwitch";
import OptionSelector from "./components/OptionSelector";
import { sendMessage } from "./websocket";
import "./App.css";

function App() {
  // stati dei controlli
  const [darkness, setDarkness] = useState(9); // parte dal minimo richiesto
  const [rain, setRain] = useState(0);
  const [turbolence, setTurbolence] = useState(0);
  const [lightningOn, setLightningOn] = useState(false);
  const [rumblingOn, setRumblingOn] = useState(false);
  const [masksOn, setMasksOn] = useState(false);
  const [voice, setVoice] = useState("calm");
  const [forcedVoice, setForcedVoice] = useState("calm");

  // ‚úÖ booleano che diventa true SOLO dopo che Darkness ha superato 10.5 almeno una volta
  const [hasExposureCrossed, setHasExposureCrossed] = useState(false);

  // vincoli base (enable/disable dei controlli)
  const allDisabled = darkness < 10.5;

  const rainEnabled      = !allDisabled && darkness > 10.5;
  const lightningEnabled = rainEnabled && rain > 10000;
  const turbEnabled      = rainEnabled && rain > 10000;
  const rumblingEnabled  = rainEnabled && rain > 10000;

  const worriedEnabled   = rainEnabled && rain > 10000;
  const masksEnabled     = turbEnabled && turbolence > 0.005;
  const panicEnabled     = turbEnabled && turbolence > 0.005;

  // üîí set "tutti gli altri parametri attivi/diversi da minimo"
  const othersAllActive = useMemo(() => {
    const voiceActive = voice !== "calm";
    return (
      rain > 10000 &&
      lightningOn &&
      turbolence > 0.005 &&
      rumblingOn &&
      masksOn &&
      voiceActive
    );
  }, [rain, lightningOn, turbolence, rumblingOn, masksOn, voice]);

  // üîê lock: si attiva solo se abbiamo superato 10.5 almeno una volta E tutti gli altri sono attivi
  const exposureLock = hasExposureCrossed && othersAllActive;

  // min dinamico per Darkness: 10.5 quando lock attivo, altrimenti 9
  const exposureMin = exposureLock ? 10.5 : 9;

  // Forza Calm quando Darkness < 10.5 (regola preesistente)
  useEffect(() => {
    if (darkness < 10.5) {
      if (voice !== "calm") {
        setVoice("calm");
        sendMessage({ type: "command", action: "toggle_param", param: "voices", value: "calm" });
      }
      setForcedVoice("calm");
    } else {
      setForcedVoice(undefined);
    }
  }, [darkness, voice]);

  // helper invio sicuro
  const safeSendToggle = (enabled, param, value) => {
    if (!enabled) return;
    sendMessage({ type: "command", action: "toggle_param", param, value });
  };
  const safeSendSlider = (enabled, param, value) => {
    if (!enabled) return;
    sendMessage({ type: "command", action: "update_param", param, value: Number(value) });
  };

  return (
    <div className="app">
      <h1 className="title">VR Control Panel</h1>
      <div className="panel">

        {/* DARKNESS */}
        <ParameterSlider
          label="Darkness"
          param="exposure"
          min={exposureMin}     // üëà blocca lo slider sotto 10.5 quando exposureLock √® true
          max={12}
          step={0.1}
          value={Math.max(darkness, exposureMin)}
          onChange={(p, v) => {
            // clamp locale al min dinamico
            const clamped = Math.max(v, exposureMin);
            setDarkness(clamped);

            // aggiorna il booleano: diventa true appena superi 10.5 una volta
            if (!hasExposureCrossed && clamped > 10.5) {
              setHasExposureCrossed(true);
            }

            safeSendSlider(true, p, clamped);
          }}
        />

        {/* RAIN */}
        <ParameterSlider
          label="Rain Intensity"
          param="rain"
          min={0}
          max={20000}
          step={100}
          value={rain}
          disabled={!rainEnabled}
          onChange={(p, v) => {
            setRain(v);
            safeSendSlider(rainEnabled, p, v);
          }}
        />

        {/* LIGHTNING */}
        <ToggleSwitch
          label="Lightning"
          param="lightning"
          disabled={!lightningEnabled}
          onChange={(p, v) => {
            setLightningOn(v);
            safeSendToggle(lightningEnabled, p, v);
          }}
        />

        {/* TURBOLENCE */}
        <ParameterSlider
          label="Turbolence"
          param="turbolence"
          min={0}
          max={0.015}
          step={0.001}
          value={turbolence}
          disabled={!turbEnabled}
          onChange={(p, v) => {
            setTurbolence(v);
            safeSendSlider(turbEnabled, p, v);
          }}
        />

        {/* RUMBLING */}
        <ToggleSwitch
          label="Rumbling"
          param="rumbling"
          disabled={!rumblingEnabled}
          onChange={(p, v) => {
            setRumblingOn(v);
            safeSendToggle(rumblingEnabled, p, v);
          }}
        />

        {/* OXYGEN MASKS */}
        <ToggleSwitch
          label="Oxygen Masks"
          param="oxygenMasks"
          disabled={!masksEnabled}
          onChange={(p, v) => {
            setMasksOn(v);
            safeSendToggle(masksEnabled, p, v);
          }}
        />

        {/* VOICES */}
        <OptionSelector
          label="Voices"
          param="voices"
          options={[
            { label: "Calm",    value: "calm",    disabled: false },
            { label: "Worried", value: "worried", disabled: !worriedEnabled || allDisabled },
            { label: "Panic",   value: "panic",   disabled: !panicEnabled   || allDisabled },
          ]}
          forceValue={forcedVoice}
          onChange={(p, v) => {
            // rispetta i vincoli
            if (v === "worried" && (!worriedEnabled || allDisabled)) return;
            if (v === "panic"   && (!panicEnabled   || allDisabled)) return;

            setVoice(v);
            safeSendToggle(true, p, v);
          }}
        />
      </div>
    </div>
  );
}

export default App;
