import React from "react";
import ParameterSlider from "./components/ParameterSlider";
import ToggleSwitch from "./components/ToggleSwitch";
import { sendMessage } from "./websocket";
import "./App.css"; // importa il CSS

function App() {
  const handleSliderChange = (param, value) => {
    sendMessage({
      type: "command",
      action: "update_param",
      param,
      value,
    });
  };

  const handleToggleChange = (param, value) => {
    sendMessage({
      type: "command",
      action: "toggle_param",
      param,
      value,
    });
  };

  return (
    <div className="app">
      <h1 className="title">Pannello di Controllo VR</h1>
      <div className="panel">
        <ParameterSlider
          label="VelocitÃ "
          param="speed"
          min={0}
          max={10}
          onChange={handleSliderChange}
        />
        <ToggleSwitch
          label="Luce Attiva"
          param="light"
          onChange={handleToggleChange}
        />
      </div>
    </div>
  );
}

export default App;
