import React, { useState} from "react";
import ParameterSlider from "./components/parameterSlider";
import ToggleSwitch from "./components/ToggleSwitch";
import OptionSelector from "./components/OptionSelector";
import { sendMessage } from "./websocket";
import "./App.css";

function App() {
  // stato locale per calcolare i vincoli (invia comunque i messaggi al server)
  const [darkness, setDarkness] = useState(10.5);
  const [rain, setRain] = useState(0);
  const [turbolence, setTurbolence] = useState(0);

  // vincoli
  const allDisabled = darkness < 10.5; // se true, tutto (tranne Darkness) è disattivato

  const rainEnabled       = !allDisabled && darkness > 10.5;
  const lightningEnabled  = rainEnabled && rain > 10000;
  const turbEnabled       = rainEnabled && rain > 10000;
  const rumblingEnabled   = rainEnabled && rain > 10000;

  const worriedEnabled    = rainEnabled && rain > 10000;
  const masksEnabled      = turbEnabled && turbolence > 0.005;
  const panicEnabled      = turbEnabled && turbolence > 0.005;

  // handler sicuri: non inviano se il controllo è disabled
  const handleToggleChange = (param, value, enabled = true) => {
    if (!enabled) return;
    sendMessage({ type: "command", action: "toggle_param", param, value });
  };

  const handleSliderChange = (param, value, enabled = true) => {
    if (!enabled) return;
    sendMessage({ type: "command", action: "update_param", param, value: Number(value) });
  };

  const handleVoiceChange = (param, value, canSelect = true) => {
    if (!canSelect) return;
    sendMessage({ type: "command", action: "toggle_param", param, value });
  };

  return (
    <div className="app">
      <h1 className="title">VR Control Panel</h1>
      <div className="panel">
        {/* DARKNESS: SEMPRE ABILITATO */}
        <ParameterSlider
          label="Darkness"
          param="exposure"
          min={9}
          max={12}
          step={0.1}
          value={darkness}
          onChange={(p, v) => {
            setDarkness(v);
            handleSliderChange(p, v, true);
          }}
        />

        {/* RAIN: abilitato solo se darkness > 10.5 */}
        <ParameterSlider
          label="Rain Intensity"
          param="rain"
          min={0}
          max={20000}
          step={100}
          value={rain}
          disabled={!rainEnabled}
          onChange={(p, v) => {
            setRain(v);
            handleSliderChange(p, v, rainEnabled);
          }}
        />

        {/* LIGHTNING: abilitato se rain > 10000 */}
        <ToggleSwitch
          label="Lightning"
          param="lightning"
          disabled={!lightningEnabled}
          onChange={(p, v) => handleToggleChange(p, v, lightningEnabled)}
        />

        {/* TURBOLENCE: abilitato se rain > 10000 */}
        <ParameterSlider
          label="Turbolence"
          param="turbolence"
          min={0}
          max={0.015}
          step={0.001}
          value={turbolence}
          disabled={!turbEnabled}
          onChange={(p, v) => {
            setTurbolence(v);
            handleSliderChange(p, v, turbEnabled);
          }}
        />

        {/* RUMBLING: abilitato se rain > 10000 */}
        <ToggleSwitch
          label="Rumbling"
          param="rumbling"
          disabled={!rumblingEnabled}
          onChange={(p, v) => handleToggleChange(p, v, rumblingEnabled)}
        />

        {/* OXYGEN MASKS: abilitate se turbolence > 0.005 */}
        <ToggleSwitch
          label="Oxygen Masks"
          param="oxygenMasks"
          disabled={!masksEnabled}
          onChange={(p, v) => handleToggleChange(p, v, masksEnabled)}
        />

        {/* VOICES: calm sempre selezionabile; worried se rain > 10000; panic se turbolence > 0.005 */}
        <OptionSelector
          label="Voices"
          param="voices"
          options={[
            { label: "Calm",    value: "calm",    disabled: false },
            { label: "Worried", value: "worried", disabled: !worriedEnabled },
            { label: "Panic",   value: "panic",   disabled: !panicEnabled },
          ]}
          onChange={(p, v) => {
            // trova l'opzione e verifica se non è disabilitata
            const opt = [{ value: "calm", disabled: false },
                         { value: "worried", disabled: !worriedEnabled },
                         { value: "panic", disabled: !panicEnabled }].find(o => o.value === v);
            const can = opt ? !opt.disabled : false;
            handleVoiceChange(p, v, can);
          }}
        />
      </div>
    </div>
  );
}

export default App;
