import React, { useState, useEffect, useMemo } from "react";
import ParameterSlider from "./components/parameterSlider";
import ToggleSwitch from "./components/ToggleSwitch";
import OptionSelector from "./components/OptionSelector";
import { sendMessage } from "./websocket";
import "./App.css";

function App() {
  // Stati dei controlli
  const [darkness, setDarkness] = useState(9); // parte dal minimo richiesto
  const [rain, setRain] = useState(0);
  const [turbolence, setTurbolence] = useState(0);
  const [lightningOn, setLightningOn] = useState(false);
  const [rumblingOn, setRumblingOn] = useState(false);
  const [masksOn, setMasksOn] = useState(false);
  const [voice, setVoice] = useState("calm");
  const [forcedVoice, setForcedVoice] = useState("calm"); // forza Calm quando necessario

  // Flag: diventa true solo dopo che Darkness ha superato 10.5 almeno una volta
  const [hasExposureCrossed, setHasExposureCrossed] = useState(false);

  // Abilitazioni base
  const allDisabled = darkness < 10.5;

  const rainEnabled      = !allDisabled && darkness > 10.4;
  const lightningEnabled = rainEnabled && rain > 10000;
  const turbEnabled      = rainEnabled && rain > 10000;
  const rumblingEnabled  = rainEnabled && rain > 10000;

  const worriedEnabled   = rainEnabled && rain > 10000;
  const masksEnabled     = turbEnabled && turbolence > 0.005;
  const panicEnabled     = turbEnabled && turbolence > 0.005;

  // Tutti gli altri parametri attivi/diversi da minimo
  const othersAllActive = useMemo(() => {
    const voiceActive = voice !== "calm";
    return (
      rain > 10000 &&
      lightningOn &&
      turbolence > 0.005 &&
      rumblingOn &&
      masksOn &&
      voiceActive
    );
  }, [rain, lightningOn, turbolence, rumblingOn, masksOn, voice]);

  // Lock: attivo solo se abbiamo superato 10.5 una volta e tutti gli altri sono attivi
  const exposureLock = hasExposureCrossed && othersAllActive;

  // Forza Calm quando Darkness < 10.5
  useEffect(() => {
    if (darkness < 10.5) {
      if (voice !== "calm") {
        setVoice("calm");
        sendMessage({ type: "command", action: "toggle_param", param: "voices", value: "calm" });
      }
      setForcedVoice("calm");
    } else {
      setForcedVoice(undefined);
    }
  }, [darkness, voice]);

  // Helper invio
  const safeSendToggle = (enabled, param, value) => {
    if (!enabled) return;
    sendMessage({ type: "command", action: "toggle_param", param, value });
  };
  const safeSendSlider = (enabled, param, value) => {
    if (!enabled) return;
    sendMessage({ type: "command", action: "update_param", param, value: Number(value) });
  };

  return (
    <div className="app">
      <h1 className="title">VR Control Panel</h1>
      <div className="panel">
        {/* DARKNESS (min/max visuali invariati, ma se lock attivo non puÃ² scendere sotto 10.5) */}
        <ParameterSlider
          label="Darkness"
          param="exposure"
          min={9}
          max={12}
          step={0.1}
          value={darkness}
          onChange={(p, v) => {
            // Se lock attivo e si prova a scendere sotto 10.5 â†’ blocca a 10.5
            const next = (exposureLock && v < 10.5) ? 10.5 : v;
            setDarkness(next);
            if (!hasExposureCrossed && next > 10.5) setHasExposureCrossed(true);
            safeSendSlider(true, p, next);
          }}
        />

        {/* RAIN: abilitato solo se darkness > 10.5 */}
        <ParameterSlider
          label="Rain Intensity"
          param="rain"
          min={0}
          max={20000}
          step={100}
          value={rain}
          disabled={!rainEnabled}
          onChange={(p, v) => {
            setRain(v);
            safeSendSlider(rainEnabled, p, v);
          }}
        />

        {/* LIGHTNING: abilitato se rain > 10000 */}
        <ToggleSwitch
          label="Lightning"
          param="lightning"
          disabled={!lightningEnabled}
          onChange={(p, v) => {
            setLightningOn(v);
            safeSendToggle(lightningEnabled, p, v);
          }}
        />

        {/* TURBOLENCE: abilitato se rain > 10000 */}
        <ParameterSlider
          label="Turbolence"
          param="turbolence"
          min={0}
          max={0.015}
          step={0.001}
          value={turbolence}
          disabled={!turbEnabled}
          onChange={(p, v) => {
            setTurbolence(v);
            safeSendSlider(turbEnabled, p, v);
          }}
        />

        {/* RUMBLING: abilitato se rain > 10000 */}
        <ToggleSwitch
          label="Rumbling"
          param="rumbling"
          disabled={!rumblingEnabled}
          onChange={(p, v) => {
            setRumblingOn(v);
            safeSendToggle(rumblingEnabled, p, v);
          }}
        />

        {/* OXYGEN MASKS: abilitate se turbolence > 0.005 */}
        <ToggleSwitch
          label="Oxygen Masks"
          param="oxygenMasks"
          disabled={!masksEnabled}
          onChange={(p, v) => {
            setMasksOn(v);
            safeSendToggle(masksEnabled, p, v);
          }}
        />

        {/* VOICES:
            - Calm sempre disponibile (e forzato se darkness < 10.5)
            - Worried abilitato se rain > 10000
            - Panic   abilitato se turbolence > 0.005
        */}
        <OptionSelector
          label="Voices"
          param="voices"
          options={[
            { label: "Calm",    value: "calm",    disabled: false },
            { label: "Worried", value: "worried", disabled: allDisabled || !worriedEnabled },
            { label: "Panic",   value: "panic",   disabled: allDisabled || !panicEnabled },
          ]}
          forceValue={forcedVoice}
          onChange={(p, v) => {
            if (v === "worried" && (allDisabled || !worriedEnabled)) return;
            if (v === "panic"   && (allDisabled || !panicEnabled)) return;
            setVoice(v);
            safeSendToggle(true, p, v);
          }}
        />
      </div>
    </div>
  );
}

export default App;
