import React, { useState } from "react";

function ParameterSlider({ label, param, min, max, step, onChange, value, disabled = false }) {
  const [internal, setInternal] = useState(value ?? (min + max) / 2);

  const val = value !== undefined ? value : internal;

  const handleChange = (e) => {
    const v = parseFloat(e.target.value);
    if (value === undefined) setInternal(v); // se non è controllato dall'alto
    if (!disabled) onChange(param, v);
  };

  return (
    <div className={`control ${disabled ? "disabled" : ""}`}>
      <label>
        {label}: {param === "turbolence" ? val.toFixed(3) : val}
      </label>
      <input
        type="range"
        min={min}
        max={max}
        step={step}
        value={val}
        onChange={handleChange}
        disabled={disabled}
      />
    </div>
  );
}

export default ParameterSlider;
